import React from 'react';
import Menu from "./Menu";

class Recipe extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          recipe: 'Spaghetti',
          ingredients: ['tomato', 'pasta'],
          instructions: ['cook pasta', 'make sauce']

        };
      }

      list = (array) =>{
          return array.map(item=>(
              <li>{item}</li>
          ));
      }

      myButtonHandler = () => {
        alert ("Add " + this.state.recipe + " to favorite recipe");
      }

      render() {
        return (
        <div>
                <Menu className = {"menu"}/>

          <h1> Recipe </h1>
          <h2> {this.state.recipe} </h2>
          <p>Ingredients:</p>
          {this.list(this.state.ingredients)}
          <p>Instructions:</p>
          {this.list(this.state.instructions)}
          <button onClick={this.myButtonHandler}>Add to Favorite</button>
          </div>



        );
      }
    }

  export default Recipe;
