export default function getRecipes(include, exclude, pantry, count) {
  const API_URL = "http://localhost:4567/b/recipe";

  const params = {include, exclude, pantry, count}
   fetch(API_URL, {
    method: "POST",
    headers: {
      "content-type": "application/json"
    },
    body: JSON.stringify(params)
  })
    .then(res => res.json)
    .catch("Error: Something went wrong.");
}
