import React from 'react';
import Rootstore from './Rootstore';



class Signup extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          username: '',
          password: null,
        };
      }

      mySubmitHandler = (event) => {

        event.preventDefault();
        alert("You are submitting " + this.state.username + this.state.password);
        console.log(this.props.store.Instance.getUser());

        this.props.store.Instance.setUser(this.state.username);
        console.log(this.props.store.Instance.getUser());
        // window.location.href="/search";

      }

      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }

     myButtonHandler = () => {
        alert ("Signup page");
        alert(this.props.store.getUser());
        window.location.href="/signup";

     }

      render() {
        return (
        <div>
          <form onSubmit={this.mySubmitHandler}>
          <h1> Login </h1>
          <p>Username:</p>
          <input
            type='text'
            name='username'
            onChange={this.myChangeHandler}
          />
          <p>Password:</p>
          <input
            type='text'
            name='password'
            onChange={this.myChangeHandler}
          />
          <input
            type='submit'
          />
          </form>
          <p>New to Reverse Recipe?:</p>
        <button onClick={this.myButtonHandler}>Click here!</button>
          </div>



        );
      }
    }

  export default Signup;
