import React from 'react';
import { getPantry, getRestrictions, getSuggestions } from './Window';
import Menu from "./Menu";
import './Decoration.css';


class Search extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          recipe: '',
          pantry: getPantry(),
          restrictions: getRestrictions(),
          suggestion:getSuggestions()
        };
      }

      mySubmitHandler = (event) => {
        event.preventDefault();
        alert("You are submitting " + this.state.recipe);

      }

      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }


      myRemoveHandler = (a) => {
        alert ("remove " + a);

     }

     mySelectHandler = (a) => {
        alert ("select " + a);
        window.location.href="/recipe";


     }

      list = (array) =>{
        return array.map(item=>(
            <li>{item} </li> 
        ));
    }

    listSelect = (array) =>{
        return array.map(item=>(
            <li onClick={() => this.mySelectHandler(item)}>{item}
            </li> 
        ));
    }


      render() {
        return (
        <div>
                <Menu/>
          <form onSubmit={this.mySubmitHandler}>
        <h1> Search </h1>

          <p>Search:</p>
          <input className={"searchBar"}
            type='text'
            name='recipe'
            onChange={this.myChangeHandler}
          />
          <input
            type='submit'
          />
          </form>
          <column className = {"column2"}>

          <p>Your current pantry:</p>
          {this.list(this.state.pantry)}

          <p>Your current list of restrictions:</p>
          {this.list(this.state.restrictions)}
          </column>

          <column className = {"column2"}>

          <p>Suggestion:</p>
          {this.listSelect(this.state.suggestion)}
          </column>
          </div>



        );
      }
    }

  export default Search;
