import React from 'react';
import { getPantry, getRestrictions, getSuggestions, getAutoFill1, getAutoFill2 } from './Window';
import Menu from "./Menu";
import './Decoration.css';


class Search extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          // recipe: '',
          pantry: null,
          restrictions: null,
          suggestion:null,
          loading: true,
          test:true, //delete later
        };
        this.myChangeHandler = this.myChangeHandler.bind(this);

      }

      async componentDidMount(){
        //everything
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({loading:false, pantry:getPantry(), restrictions:getRestrictions(), suggestion:getSuggestions()});
      }


      async myChangeHandler (event) {
        let nam = event.target.name;
        let val = event.target.value;

        //send text and return autofill
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);

        this.setState({[nam]: val});

        //delete later
        if (this.state.test){
          this.setState({suggestion: getAutoFill1(), test:false});
        } else{
          this.setState({suggestion: getAutoFill2(), test:true});

        }

      }

     async mySelectHandler (recipe) {

      //set current recipe to clicked recipe and go to recipe
      const url = "https://api.randomuser.me/"
      const response = await fetch(url);
      const data = await response.json();
      console.log(data);
      window.location.href = "/recipe";

    }

      list = (array) =>{
        return array.map(item=>(
            <li className={noSelect}>{item} </li> 
        ));
    }

    listSelect = (array) =>{
        return array.map(item=>(
            <li  onClick={() => this.mySelectHandler(item)}>{item}
              <h4>You have ?/? ingredients</h4>
            </li> 
        ));
    }


      render() {
        return (
        <div>
                <Menu/>

                {this.state.loading ? (<p>loading...</p>):
                (  this.state.pantry === null||this.state.restrictions ===null||this.state.suggestion === null ? 
                (<p>loading failed</p>) : (

          <div>
          <form >
        <h1> Search </h1>

          <p>Search:</p>
          <input className={"searchBar"}
            type='text'
            name='recipe'
            onChange={this.myChangeHandler}
          />

          </form>
          <column className = {"column2"}>

          <p>Your current pantry:</p>
          <div className = {"list2"}>

          {this.list(this.state.pantry)}
          </div>

          <p>Your current list of restrictions:</p>
          <div className = {"list2"}>

          {this.list(this.state.restrictions)}
          </div>
          </column>


          <column className = {"column2"}>

          <p>Suggestion:</p>
          <div className = {"list2"}>

          {this.listSelect(this.state.suggestion)}
          </div>
          </column>
          </div>
          ))}

          </div>



        );
      }
    }

  export default Search;
