import React from 'react';
import Menu from "./Menu";

class Recipe extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          recipe: null,
          ingredients: null,
          instructions: null,
          liked: null,
          loading: true
        };
      }

      async componentDidMount(){
        //try loading pantry
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({loading:false, liked: false, recipe: 'Spaghetti', ingredients: ['tomato', 'pasta'], instructions: ['cook pasta', 'make sauce']});
      }

      list = (array) =>{
          return array.map(item=>(
              <li className={'noSelect'}>{item}</li>
          ));
      }


      render() {

        return (
        <div>
                <Menu />

          <h1> Recipe </h1>
          {this.state.loading ? (<p>loading...</p>):
          ( this.state.liked === null || this.state.ingredients === null||!this.state.instruction ===null||this.state.recipe === null ? 
          (<p>loading failed</p>) : (



          <div>
          <h1> {this.state.recipe} </h1>
          <column className = {"column2"}>

          <p>Instructions:</p>
          <div className = {"list2"}>

          {this.list(this.state.instructions)}
          </div>
      <button onClick={this.myButtonHandler}>{this.state.liked === false ? "Add to Favorite" : "Remove from Favorite"}</button>

          </column>
          <column className = {"column2"}>
          <p>Ingredients:</p>
          <div className = {"list2"}>

          {this.list(this.state.ingredients)}
          </div>
          </column>
          </div>
          ))}

          </div>



        );
      }
    }

  export default Recipe;
