import React from 'react';
import { getRestrictions, getAutoFill1, getAutoFill2 } from './Window';
import Menu from "./Menu";
import './Decoration.css';


class Restriction extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          restrictions: null,
          itemToAdd: '',
          loading: true,
          suggestion: null,
          test: true
        };
        this.mySubmitHandler = this.mySubmitHandler.bind(this);
        this.myButtonHandler = this.myButtonHandler.bind(this);
        this.myChangeHandler = this.myChangeHandler.bind(this);

      }

      async componentDidMount(){
        //try loading restrictions
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({loading:false, restrictions:getRestrictions()});
      }

      async mySubmitHandler (event) {

        //try to add item to restriction list
        event.preventDefault();
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        alert("Added " + this.state.itemToAdd + "to list of restrictions");
      }

      async myChangeHandler(event) {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});

        //send text and return autofill
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);

        this.setState({[nam]: val});

        //delete later
        if (this.state.test){
          this.setState({suggestion: getAutoFill1(), test:false});
        } else{
          this.setState({suggestion: getAutoFill2(), test:true});

        }

      }

      async myButtonHandler(item) {
        //remove from pantry and set pantry
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        alert("removed "+item+" from restrictions" );


     }

      list = (array) =>{
          return array.map(item=>(
              <li>{item}
              <button onClick={() => this.myButtonHandler(item)}> X </button>
              </li> 
          ));
      }

      render() {
        return (
        <div>
                <Menu/>

          <h1> Restrictions </h1>

          <form onSubmit={this.mySubmitHandler}>
          <p>Add to list of restrictions:</p>
          <input
            type='text'
            name='itemToAdd'
            onChange={this.myChangeHandler}
          />
          <input
            type='submit'
          />
          </form>

          <p>Your current list of restrictions:</p>
          {this.state.loading ? (<p>loading...</p>):( !this.state.restrictions ? (<p>loading failed</p>) : (this.list(this.state.restrictions)))}

          </div>



        );
      }
    }

  export default Restriction;
