import React from 'react';

class Menu extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        page: ''
      };
    }
    changeHandler = (event) => {
        event.preventDefault();
        window.location.href="/"+event.target.value;
        this.setState({page:event.target.value}, () => {this.afterUpdate()})

      }

      afterUpdate = ()=>{
        alert("changed to " + this.state.page);


      }
    render() {
      return (
        <form>
        <h1> Menu </h1>
        <select onChange={this.changeHandler}>
            <option value=''>Select...</option>
          <option value='search'>Search</option>
          <option value='favorite'>Favorite Recipes</option>
          <option value='pantry'>Pantry</option>
          <option value='restrictions'>Restrictions</option>
          <option value='signup'>Sign out</option>
        </select>
        </form>

      );
    }
  }
  export default Menu;
