import React from 'react';
import {getName, changeName} from './Window.js'




class Signup extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          username: this.props.user,
          password: null,
          comfirmPassword: null,
          recipe: this.props.recipe
        };
      }

      mySubmitHandler = (event) => {
        event.preventDefault();

        //if sign is is successful
        changeName(this.state.username);
        this.setState({username: getName()});
        this.setState({recipe: getRecipe()});

        window.location.href="/welcome";
      }

      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }

     myButtonHandler = () => {
        window.location.href="/login";

     }

      render() {
        return (
        <div>
          <form onSubmit={this.mySubmitHandler}>
          <h1> Sign Up </h1>
          <p>Username:</p>
          <input
            type='text'
            name='username'
            onChange={this.myChangeHandler}
          />
          <p>Password:</p>
          <input
            type='text'
            name='password'
            onChange={this.myChangeHandler}
          />
      <p>Comfirm Password:</p>
          <input
            type='text'
            name='comfirmPassword'
            onChange={this.myChangeHandler}
          />
          <input
            type='submit'
          />
          </form>
          <p>Already have an account?:</p>
        <button onClick={this.myButtonHandler}>Click here!</button>
          </div>



        );
      }
    }


  export default Signup;
