import React from 'react';
import { setRecipe, getRecipe } from './Window';

class Favorite extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          favorite: ['Spaghetti', 'pancakes', 'cheese burger'],
        };
      }

     mySelectHandler = (recipe) => {
       setRecipe(recipe);
     }



    listSelect = (array) =>{
        return array.map(item=>(
            <li onClick={() => this.mySelectHandler(item)}>{item}
            </li> 
        ));
    }


      render() {
        return (
        <div>
          <h1> Favorite Recipes </h1>

          {this.listSelect(this.state.favorite)}

          </div>



        );
      }
    }

  export default Favorite;
