import React from 'react';
import {getName, changeName} from './Window.js'




class Signup extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          fail: false,
          username: null,
          password: null,
          comfirmPassword: null,
          empty: false,
          mismatch:false
        };
        this.mySubmitHandler = this.mySubmitHandler.bind(this);

      }

      async mySubmitHandler (event) {
        event.preventDefault();

        if (this.state.username == null || this.state.password == null || this.state.comfirmPassword == null){
          this.setState({empty: true});
        }
        else{
          this.setState({empty: false});
          if (this.state.password === this.state.comfirmPassword){
            this.setState({});
          }
        const url = "https://api.randomuser.me/";
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);

        //if log in is successful
        this.setState({fail:false});
        // window.location.href="/welcome";

        //else 
        this.setState({fail:true});
        }
      }

      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }

     myButtonHandler = () => {
        window.location.href="/login";

     }

      render() {
        return (
        <div>
          <form onSubmit={this.mySubmitHandler}>
          <h1> Sign Up </h1>
          {this.state.fail ? <p> Username is taken. Please try again. </p> : <></>}

          <p>Username:</p>
          <input
            type='text'
            name='username'
            onChange={this.myChangeHandler}
          />
          <p>Password:</p>
          <input
            type='text'
            name='password'
            onChange={this.myChangeHandler}
          />
      <p>Comfirm Password:</p>
          <input
            type='text'
            name='comfirmPassword'
            onChange={this.myChangeHandler}
          />
          <input
            type='submit'
          />
          </form>
          <p>Already have an account?:</p>
        <button onClick={this.myButtonHandler}>Click here!</button>
          </div>



        );
      }
    }


  export default Signup;
