import React from 'react';
import { getPantry } from './Window';
import Menu from "./Menu";
import './Decoration.css';


class Pantry extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          item: null,
          itemToAdd: '',
          loading: true

        };
        this.mySubmitHandler = this.mySubmitHandler.bind(this);
        this.myButtonHandler = this.myButtonHandler.bind(this);

      }

      async componentDidMount(){
        //try loading pantry
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({loading:false, item:getPantry()});
      }

      async mySubmitHandler(event) {

        //add to pantry and set pantry
        event.preventDefault();
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        alert("added "+this.state.itemToAdd+" to pantry" );
      }

      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }

      async myButtonHandler(item) {
        //remove from pantry and set pantry
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        alert("removed "+item+" to pantry" );


     }


      list = (array) =>{
          return array.map(item=>(
              <li>{item}
              <button onClick={() => this.myButtonHandler(item)}> X </button>
              </li> 
          ));
      }

      render() {
        return (
        <div>
                <Menu/>

          <h1> Pantry </h1>

          <form onSubmit={this.mySubmitHandler}>
          <p>Add to pantry:</p>
          <input
            type='text'
            name='itemToAdd'
            onChange={this.myChangeHandler}
          />
          <input
            type='submit'
          />
          </form>

          <p>Your current pantry:</p>
          {this.state.loading ? (<p>loading...</p>):( !this.state.item ? (<p>loading failed</p>) : (this.list(this.state.item)))}

          </div>



        );
      }
    }

  export default Pantry;
