import React from 'react';
import { getPantry } from './Window';
import Menu from "./Menu";
import './Decoration.css';


class Pantry extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          item: getPantry(), //change to null later
          itemToAdd: '',
          loading: true

        };
      }

      async componentDidMount(){
        //try loading pantry
        const url = "https://api.randomuser.me/"
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({loading:false});
      }

      mySubmitHandler = (event) => {
        event.preventDefault();
        alert("You are submitting " + this.state.itemToAdd);
      }

      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }

      myButtonHandler = (a) => {
        alert ("remove " + a);

     }


      list = (array) =>{
          return array.map(item=>(
              <li>{item}
              <button onClick={() => this.myButtonHandler(item)}> X </button>
              </li> 
          ));
      }

      render() {
        return (
        <div>
                <Menu/>

          <h1> Pantry </h1>

          <form onSubmit={this.mySubmitHandler}>
          <p>Add to pantry:</p>
          <input
            type='text'
            name='itemToAdd'
            onChange={this.myChangeHandler}
          />
          <input
            type='submit'
          />
          </form>

          <p>Your current pantry:</p>
          {this.state.loading ? (<p>loading...</p>):( !this.state.item ? (<p>loading failed</p>) : (this.listSelect(this.state.item)))}

          </div>



        );
      }
    }

  export default Pantry;
