import React from 'react';

class Restriction extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          restriction: ['nuts', 'soy'],
          itemToAdd: ''
        };
      }

      mySubmitHandler = (event) => {
        event.preventDefault();
        alert("You are submitting " + this.state.itemToAdd);
      }

      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }

      myButtonHandler = (a) => {
        alert ("remove " + a);

     }


      list = (array) =>{
          return array.map(item=>(
              <li>{item}
              <button onClick={() => this.myButtonHandler(item)}> Remove </button>
              </li> 
          ));
      }

      render() {
        return (
        <div>
          <h1> Restrictions </h1>

          <form onSubmit={this.mySubmitHandler}>
          <p>Add to list of restrictions:</p>
          <input
            type='text'
            name='itemToAdd'
            onChange={this.myChangeHandler}
          />
          <input
            type='submit'
          />
          </form>

          <p>Your current list of restrictions:</p>
          {this.list(this.state.item)}

          </div>



        );
      }
    }

  export default Restriction;
