import React from 'react';

class Menu extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        page: '',
        pages : ['search', 'favorite','pantry', 'restriction', 'logout']
      };
    }
    clickHandler = (newPage) => {
        this.setState({page:newPage});
        alert("changed to " + this.state.page);
      }

      listSelect = (array) =>{
        return array.map(item=>(
            <li onClick={() => this.mySelectHandler(item)}>{item}
            </li> 
        ));
    }

    mySelectHandler = (a) => {
        alert ("select " + a);

     }

    render() {
      return (
          <div>
        <h1> Menu </h1>
        {this.listSelect(this.state.pages)}
        </div>

      );
    }
  }
  export default Menu;
