      super(props);
      this.loadData();
    }


    loadData = () => {
        //load data here
        setFavorite(['Spaghetti', 'pancakes', 'cheese burger']);
        console.log(getFavorite());
        setPantry(['tomato', 'eggs', 'milk']);
        setRecipe("apple");
        setRestrictions(['nuts', 'soy']);
        // this.next();
