import React from 'react';
import { setRecipe, getRecipe, getFavorite } from './Window';
import Menu from "./Menu";

class Favorite extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      favorite: null,
      loading: true
    };

  }

  async componentDidMount(){

    //input none
    //output list of favorite
    const url = "https://api.randomuser.me/"
    const response = await fetch(url, {
      method: 'POST',
      body: JSON.stringify({
        command: "favorite"
      })
    })
    const data = await response.json();
    console.log(data);
    this.setState({loading:false, favorite:getFavorite()});
  }

  async mySelectHandler (recipe) {

    //set current recipe to clicked recipe and go to recipe
    //input: selected recipe
    //function:: set current recipe
    const url = "https://api.randomuser.me/"
    const response = await fetch(url);
    const data = await response.json();
    console.log(data);
    window.location.href = "/recipe";

  }



  listSelect = (array) => {
    return array.map(item => (
      <li  onClick={() => this.mySelectHandler(item)}>{item}
      <h4>You have ?/? ingredients</h4>
    </li> 
    ));
  }


  render() {
    return (
      <div>
        <Menu />

        <h1> Favorite Recipes </h1>
        {this.state.loading ? (<p>loading...</p>):( !this.state.favorite ? (<p>loading failed</p>) : (this.listSelect(this.state.favorite)))}
      </div>



    );
  }
}

export default Favorite;
