import React from 'react';

import { connect } from 'react-redux';
import addToCart  from './cartAction'
import cartReducer from './cartReducer';




class Signup extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          username: '',
          password: null,
          comfirmPassword: null
        };
      }

      mySubmitHandler = (event) => {
        event.preventDefault();
        this.props.addToCart(this.state.username);

        // console.log(this.props.store.Instance().getUser());
        // this.props.store.Instance().setUser(this.state.username);
        // console.log(this.props.store.Instance().getUser());
        // window.location.href="/search";
        alert("You are submitting " + this.state.username + this.state.password + this.state.comfirmPassword);
      }

      myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
      }

     myButtonHandler = () => {
        alert ("Login page");
        window.location.href="/login";

     }

      render() {
        return (
        <div>
          <form onSubmit={this.mySubmitHandler}>
          <h1> Sign Up {this.props.usernameTest}{cartReducer.getState().user}</h1>
          <p>Username:</p>
          <input
            type='text'
            name='username'
            onChange={this.myChangeHandler}
          />
          <p>Password:</p>
          <input
            type='text'
            name='password'
            onChange={this.myChangeHandler}
          />
      <p>Comfirm Password:</p>
          <input
            type='text'
            name='comfirmPassword'
            onChange={this.myChangeHandler}
          />
          <input
            type='submit'
          />
          </form>
          <p>Already have an account?:</p>
        <button onClick={this.myButtonHandler}>Click here!</button>
          </div>



        );
      }
    }
    const mapStateToProps1 = (state)=>{
      return {
        usernameTest: state.user
      }
    }


    const mapDispatchToProps1= (dispatch)=>{
      return{
         addToCart: (id)=>{dispatch(addToCart(id))}
           }
      }

  export default connect(mapStateToProps1, mapDispatchToProps1) (Signup);
