import React from 'react';

class Recipe extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          recipe: 'Spaghetti',
          ingredients: ['tomato', 'pasta'],
          instructions: ['cook pasta', 'make sauce']

        };
      }

      list = (array) =>{
          return array.map(item=>(
              <li>{item}</li>
          ));
      }

      render() {
        return (
        <div>
          <h1> Recipe </h1>
          <h2> {this.state.recipe} </h2>

          <p>Ingredients:</p>
          {this.list(this.state.ingredients)}
          <p>Instructions:</p>
          {this.list(this.state.instructions)}
          </div>



        );
      }
    }

  export default Recipe;
