import { ADD_TO_CART} from 'cartAction.js';

const initState = {
    user: "HELLO",
    pantry: [],
    restrictions:[],
    favorite :[],
    recipe: ""
}

const cartReducer= (state = initState,action)=>{

    if (action.type === ADD_TO_CART){
        return {
            ...state,
            user: action.id
        }
    } else {

    return state;
    }

}
export default cartReducer;
